code.global:
/* We will initialise the mic from our computer to work in the sketch. This will allow us to use mic levels and mic inputs to change our graphics.

Add a variable called "mic" and initialize it in the setup. */

let mic, micLevel;

code.setup:
function setup() {
  createCanvas(400, 400);
  mic = new p5.AudioIn();
  mic.start();
}

code.draw:
function draw() {
  background(0);

  micLevel = mic.getLevel();
  // checks the value of micLevel and prints it in the console so you know how much to multiply it by to use it as a value in your sketch
  console.log(micLevel);

  // change text color to pink
  fill("pink");
  textSize(30 + micLevel * 2000); // use micLevel to change the size of the text. Snap your fingers and see what happens to the text!
  textAlign(CENTER);
  text("CODE DRIFT", width / 2, height / 3, 20);
}

// try to add an image in the background of this sketch!

function draw() {
  // background has alpha value of 50 which means you see a streak of things moving
  background(0, 0, 255, 50); // try changing the last value and see what happens

  // frameCount is a variable in p5 that keeps count of how many frames are drawn since the sketch started running. It is a useful variable to use to change things relative to time

  // here we are using the modulo operator (%) which outputs the remainder of the division of two values. In this case, frameCount and 400.
  // This means that in this example the value of 'frameCount % 400' cycles between 0 and 400

  // noStroke(); // draw circle without black stroke
  circle(200, frameCount % 400, 30);

  textSize(30);
  fill("white");
  text("code drift game is so cool", frameCount % 400, 200);

  // console.log(frameCount % 400);
}

// can you make images loop like the text and the circle in different directions?

code.global:
/* We will initialise the mic from our computer to work in the sketch. This will allow us to use mic levels and mic inputs to change our graphics.

Add a variable called "mic" and initialize it in the setup. */

let mic,
  cam,
  y = 0;

code.setup:
function setup() {
  createCanvas(400, 400, WEBGL); // specify WEBGL in canvas parameters when you want to use a 3D canvas
  mic = new p5.AudioIn();
  mic.start();

  cam = createCapture(VIDEO); // use cam to store webcam feed
  cam.hide();

  angleMode(DEGREES); // change angle mode from radians to Degrees
}

code.draw:
function draw() {
  background("purple");

  // allows you to move your box around by clicking and dragging with mouse
  orbitControl();

  micLevel = mic.getLevel();
  // checks the value of micLevel and prints it in the console so you know how much to multiply it by to use it as a value in your sketch
  console.log(micLevel);

  rotateY(y); // rotate y by y angle

  // use cam as texture for the box
  texture(cam);
  // use micLevel value to change size of the box
  box(30 + 4000 * micLevel); // if you don't see a box when you run your sketch, check if p5 has microphone permissions, and then try increasing the number being multiplied by micLevel

  // change value of y to gradually change rotation angle
  y = y + 1;
}

function draw() {
  background(220);

  // using the if condition to check if mouseX position is on the right half or left half of the canvas
  if (mouseX > 200) {
    fill("blue");
  } else {
    fill("red");
  }
  ellipse(mouseX, mouseY, 50);
}
