code.preload:
function preload() {
  img = loadImage("myImage.png");
} //Change the path to the image file

code.draw:
function draw() {
  background(220);

  // draw the image
  image(img, 0, 0, width, height);

  // get color from the image at the mouse position
  let c = img.get(
    map(mouseX, 0, width, 0, img.width),
    map(mouseY, 0, height, 0, img.height)
  );

  // draw the circle using that color
  fill(c); // try replacing c with 'pink' or 'blue'
  noStroke();

  // circle(x position, y position, radius)
  circle(300, 200, 60); // try to change the size of the circle

  // try adding more circles
}

