  img = loadImage("a.png"); // your image
}

code.draw:
function draw() {
  background(220);

  //image(image, top left x position, top left y position, width, height)
  image(img, 0, 0, width, height); //try replacing width and height with different numbers

  // text styling
  textSize(50); //size of text
  textAlign(CENTER, CENTER);
  stroke(0);        // outline color, try changing to some other value or color
  strokeWeight(4);  // outline thickness

  //fill(red, green, blue)
  fill(255, 200, 0); // text color. try combining different values to make different colors

  //display the text with above styling
  text("hello world", width / 2, height / 2);

}

