  img = loadImage("a.png"); // your image
}

code.draw:
function draw() {
  background(200, 220, 255); // light blue, try changing the values

  //fill(red, green, blue)
  fill(255, 120, 120);   // rect fill color, try combining different values to make different colors
  stroke(0);             // rect border color, try noStroke() instead for no border or try changing to some other value or color
  strokeWeight(3);       // outline thickness 
  //rect(top left x position, top left y position, width, height)
  rect(80, 60, 240, 190); 

  //image(image, top left x position, top left y position, width, height)
  image(img, 100, 80, 200, 150); 
}

