code.setup:
function setup() {
  createCanvas(600, 400, WEBGL); // WEBGL required for box()
}

code.draw:
function draw() {
  background(220);

  // rotate the box using mouse position
  rotateY(mouseY * 0.1);
  rotateX(mouseX * 0.1);

  texture(img); // apply the image as texture

  // draw the box
  box(200); // size of the box, try changing this
}
